<?//echo '<pre>'.print_r($arResult, true).'</pre>';//$arDoctorIDs = array();foreach ($arResult['ITEMS'] as $arAction) { 	//echo '<pre>'.print_r($arAction['DISPLAY_PROPERTIES'], true).'</pre>';	if (isset($arAction['DISPLAY_PROPERTIES']['DOCTOR']))		$arDoctorIDs[$arAction['ID']] = $arAction['DISPLAY_PROPERTIES']['DOCTOR']['VALUE'];}//print_r($arDoctorIDs);if (is_array($arDoctorIDs)) {	$resDoctors = CIBlockElement::GetList(		Array(), 		Array("IBLOCK_ID"=>6, "ACTIVE"=>"Y", "ID" => $arDoctorIDs), 		false, 		Array(), 		Array("ID", "NAME", "PREVIEW_PICTURE")	);	while($obDoctors = $resDoctors->GetNextElement()) {		$arFields = $obDoctors->GetFields();		if (isset($arFields["PREVIEW_PICTURE"]))			$arFields["PREVIEW_PICTURE"] = CFile::GetFileArray($arFields['PREVIEW_PICTURE']);		$arDoctors[$arFields['ID']] = $arFields;		//print_r($arFields);	}	$arResult['DOCTORS'] = $arDoctors;}//echo '<pre>'.print_r($arResult['DOCTORS']);?>